CREATE DATABASE test;
CREATE USER 'TestUser'@'localhost' IDENTIFIED BY '123';GRANT USAGE ON *.* TO 'TestUser'@'localhost' REQUIRE NONE WITH MAX_QUERIES_PER_HOUR 0 MAX_CONNECTIONS_PER_HOUR 0 MAX_UPDATES_PER_HOUR 0 MAX_USER_CONNECTIONS 0;GRANT ALL PRIVILEGES ON `test`.* TO 'TestUser'@'localhost';
CREATE TABLE `test`.`currency_rates` ( `id` INT NOT NULL AUTO_INCREMENT , `creation_date` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `currency` VARCHAR(255) NOT NULL , `rate` DECIMAL(15,8) NOT NULL , PRIMARY KEY (`id`), INDEX (`creation_date`), INDEX (`currency`)) ENGINE = InnoDB;